-- | Resolve the value of names to IPFS.
--
-- Response example:
--
-- >
-- > {
-- >     "Path": "<string>"
-- > }
-- >
type ApiV0Resolve =
      "api" :> "v0" :> "resolve"
    -- The name to resolve.
    :> QueryParam' '[Required, Strict] "name" Text
    -- Resolve until the result is an IPFS name.
    :> QueryParam "recursive" Bool
    -- Number of records to request for DHT resolution.
